mapper.select:
SELECT p.product_id, p.shop_id, p.name, p.image_url AS cover_image_url, COALESCE(MIN(s.price), 0) AS min_price, COALESCE(SUM(oi.num), 0) AS sales, m.merchant_name AS merchant_name FROM product p
INNER JOIN product_category pc ON p.product_id = pc.product_id
INNER JOIN shop sh ON p.shop_id = sh.shop_id LEFT JOIN merchant m ON sh.merchant_id = m.merchant_id LEFT JOIN sku s ON p.product_id = s.product_id LEFT JOIN order_item oi ON s.sku_id = oi.sku_id WHERE p.status = 1 AND sh.status = 1
AND pc.category_id IN
#{categoryId}
GROUP BY p.product_id, p.shop_id, p.name, p.image_url, p.create_time, m.merchant_name ORDER BY p.create_time DESC LIMIT #{limit} OFFSET #{offset}

SELECT p.product_id, p.shop_id, p.name, p.status, p.description, p.image_url AS cover_image_url, p.create_time, p.update_time, COALESCE(MIN(s.price), 0) AS min_price, COALESCE(SUM(oi.num), 0) AS sales, MAX(pc.category_id) AS category_id FROM product p LEFT JOIN sku s ON p.product_id = s.product_id LEFT JOIN order_item oi ON s.sku_id = oi.sku_id LEFT JOIN product_category pc ON p.product_id = pc.product_id WHERE p.shop_id = #{shopId}
AND p.status = #{status}
AND pc.category_id IN
#{categoryId}
GROUP BY p.product_id, p.shop_id, p.name, p.status, p.description, p.image_url, p.create_time, p.update_time ORDER BY p.create_time DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(DISTINCT p.product_id)
FROM product p

  INNER JOIN product_category pc ON p.product_id = pc.product_id

WHERE p.shop_id = #{shopId}

  AND p.status = #{status}


  AND pc.category_id IN

    #{categoryId}
